import logo from './DELogo.png';
import './App.css';

import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";


import { Button, Form, FormGroup, Label, Input, FormText, Table } from 'reactstrap';



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewCompleted: false,
      activeItem: {
        title: "",
        description: "",
        completed: false
      },
      todoList: []
    };
  }
  componentDidMount() {
    this.refreshList();
  }
  refreshList = () => {
    axios
      .get("http://localhost:8000/api/todos/")
      .then(res => this.setState({ todoList: res.data }))
      .catch(err => console.log(err));
  };
  displayCompleted = status => {
    if (status) {
      return this.setState({ viewCompleted: true });
    }
    return this.setState({ viewCompleted: false });
  };
  renderTabList = () => {
    return (
      <div className="my-5 tab-list">
        <span
          onClick={() => this.displayCompleted(true)}
          className={this.state.viewCompleted ? "active" : ""}
        >
          complete
        </span>
        <span
          onClick={() => this.displayCompleted(false)}
          className={this.state.viewCompleted ? "" : "active"}
        >
          Incomplete
        </span>
      </div>
    );
  };
  renderItems = () => {
    const { viewCompleted } = this.state;
    const newItems = this.state.todoList.filter(
      item => item.completed === viewCompleted
    );
    return newItems.map(item => (
      <li
        key={item.id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span
          className={`todo-title mr-2 ${
            this.state.viewCompleted ? "completed-todo" : ""
          }`}
          title={item.description}
        >
          {item.title}
        </span>
        <span>
          <button
            onClick={() => this.editItem(item)}
            className="btn btn-secondary mr-2"
          >
            {" "}
            Edit{" "}
          </button>
          <button
            onClick={() => this.handleDelete(item)}
            className="btn btn-danger"
          >
            Delete{" "}
          </button>
        </span>
      </li>
    ));
  };
  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };
  handleSubmit = item => {
    this.toggle();
    if (item.id) {
      axios
        .put(`http://localhost:8000/api/todos/${item.id}/`, item)
        .then(res => this.refreshList());
      return;
    }
    axios
      .post("http://localhost:8000/api/todos/", item)
      .then(res => this.refreshList());
  };
  handleDelete = item => {
    axios
      .delete(`http://localhost:8000/api/todos/${item.id}`)
      .then(res => this.refreshList());
  };
  createItem = () => {
    const item = { title: "", description: "", completed: false };
    this.setState({ activeItem: item, modal: !this.state.modal });
  };
  editItem = item => {
    this.setState({ activeItem: item, modal: !this.state.modal });
  };
  render() {
    return (
      <main className="content">
        <h2  className="text-center my-4"><span class="contentHeader">CONVERT SOUND</span></h2>
        <div className="row ">
          <div className="col-md-10 col-sm-10 mx-auto p-0">
            <div className="card p-3">


            <div id="drop-area">
              <form class="my-form">
                <p>Upload 1 audio file with the file dialog or by dragging and dropping audio file onto the dashed region</p>
                <input type="file" id="fileElem" multiple accept="image/*" onchange="handleFiles(this.files)"/>
                <label class="button" for="fileElem">Select audio file</label>
              </form>
            </div>




            <Label for="exampleText" size="lg">File information:</Label>
            <Table size="sm">
              <thead>
                <tr>
                  <th>Item</th>
                  <th>Display</th>
                  <th>Initial value</th>
                  <th>Unit</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th scope="row">Size</th>
                  <td> </td>
                  <td>-</td>
                  <td>Kb</td>
                </tr>
                <tr>
                  <th scope="row">Bit rate</th>
                  <td> </td>
                  <td>-</td>
                  <td>kbps</td>
                </tr>
                <tr>
                  <th scope="row">Sampling frequency</th>
                  <td> </td>
                  <td>-</td>
                  <td>kHz</td>
                </tr>
                <tr>
                  <th scope="row">Byte per second</th>
                  <td> </td>
                  <td>-</td>
                  <td>Byte/s</td>
                </tr>
                <tr>
                  <th scope="row">Bit per sample</th>
                  <td> </td>
                  <td>-</td>
                  <td>Bit/sample</td>
                </tr>
                <tr>
                  <th scope="row">Length</th>
                  <td>hh:mm:ss</td>
                  <td>-:-:-</td>
                  <td>none</td>
                </tr>
                <tr>
                  <th scope="row">Creation date</th>
                  <td>y/m/d hh:mm:ss</td>
                  <td>-/-/- -:-:-</td>
                  <td>none</td>
                </tr>
                <tr>
                  <th scope="row">Update date</th>
                  <td>y/m/d hh:mm:ss</td>
                  <td>-/-/- -:-:-</td>
                  <td>none</td>
                </tr>
                <tr>
                  <th scope="row">Sound type</th>
                  <td>Stereo</td>
                  <td>-</td>
                  <td>none</td>
                </tr>
              </tbody>
            </Table>



            <Label for="exampleText" size="lg">Convert setting:</Label>
            <Table size="sm">
              <thead>
                <tr>
                  <th>Item</th>
                  <th>Display</th>
                  <th>Initial value</th>
                  <th>Unit</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th scope="row">Sampling frequency</th>
                  <td> </td>
                  <td>-</td>
                  <td>kHz</td>
                </tr>
                <tr>
                  <th scope="row">Byte per second</th>
                  <td> </td>
                  <td>-</td>
                  <td>Byte/s</td>
                </tr>
                <tr>
                  <th scope="row">Length</th>
                  <td>hh:mm:ss</td>
                  <td>-:-:-</td>
                  <td>none</td>
                </tr>
                <tr>
                  <th scope="row">Sound type</th>
                  <td>Stereo</td>
                  <td>-</td>
                  <td>none</td>
                </tr>
                <tr>
                  <th scope="row">File type</th>
                  <td>.csv</td>
                  <td>-</td>
                  <td>none</td>
                </tr>
                <tr>
                  <th scope="row">Data coversion operation</th>
                  <td>RAW</td>
                  <td>-</td>
                  <td>none</td>
                </tr>
              </tbody>
            </Table>

            <audio controls>
              <source src="horse.mp3" type="audio/mpeg"/>
            </audio>

            <div className="">
              <p class="right">
                <button onClick={this.createItem} className="btn btn-primary">
                  Convert file
                </button>
              </p>  
            </div>








            <FormGroup>
              <Label for="exampleFile">Select file</Label>
              <Input type="fType" name="fName" id="fExample" placeholder="Just select 1 file audio!" />
              <Input type="file" name="file" id="exampleFile" />
              <FormText color="muted">
                Open or drag file to here
              </FormText>
            </FormGroup>




              {this.renderTabList()}
              <ul className="list-group list-group-flush">
                {this.renderItems()}
              </ul>
            </div>
          </div>
        </div>
        {this.state.modal ? (
          <Modal
            activeItem={this.state.activeItem}
            toggle={this.toggle}
            onSave={this.handleSubmit}
          />
        ) : null}
         <h2 className=" text-center my-4"><img height = '30' src={logo} className="App-logo" alt="logo" /></h2>
      </main>
    );
  }
}
export default App;
